// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract SLABillingLocal {
    uint256 public constant serviceCost = 1 ether; // Fixed service cost for all services
    uint256 public constant penaltyRate = 0.001 ether; // Penalty rate per breach
    uint256 public constant breachCount = 3; // Number of breaches per user (constant in this scenario)
    uint256 public constant penaltyScore = 11; // Penalty score for each user (constant in this scenario)

    mapping(address => uint256) public penaltyAmounts; // Stores calculated penalty amounts in Ether for each user address
    mapping(address => uint256) public finalBills; // Stores final bill amounts per user address

    event BillCalculated(address indexed userAddress, uint256 finalBill);
    event FundsTransferred(address indexed from, address indexed to, uint256 amount);

    // Calculate and store the penalty amount as Ether for a given user address
    function recordPenaltyAmount(address userAddress) public {
        uint256 penalty = penaltyScore * breachCount * penaltyRate; // Total penalty based on fixed score and count
        penaltyAmounts[userAddress] = penalty;
    }

    // Calculate and store the final bill for a given user address
    function calculateFinalBill(address userAddress) public {
        uint256 finalBill = serviceCost - penaltyAmounts[userAddress];
        finalBills[userAddress] = finalBill;
        emit BillCalculated(userAddress, finalBill);
    }

    function getBillDetails(address userAddress) public view returns (uint256 penaltyAmount, uint256 finalBill) {
    return (penaltyAmounts[userAddress], finalBills[userAddress]);
    }

    // Transfer the calculated bill amount to the service provider
    function transferFunds(address payable recipient, uint256 amount) public payable {
        require(msg.value >= amount, "Insufficient funds sent");
        recipient.transfer(amount);
        emit FundsTransferred(msg.sender, recipient, amount);

        if (msg.value > amount) {
            payable(msg.sender).transfer(msg.value - amount); // Refund any excess
        }
    }
}

